/**
 * @param {DashboardMode} dashboardMode.
 * @param actions {Object} - A mapping of TopNavIds to an action function that should run when the
 * corresponding top nav is clicked.
 * @param hideWriteControls {boolean} if true, does not include any controls that allow editing or creating objects.
 * @return {Array<kbnTopNavConfig>} - Returns an array of objects for a top nav configuration, based on the
 * mode.
 */
function getTopNavConfig(dashboardMode, actions, hideWriteControls) {
  switch (dashboardMode) {
    case _dashboard_view_mode.DashboardViewMode.VIEW:
      return hideWriteControls ? [getFullScreenConfig(actions[_top_nav_ids.TopNavIds.FULL_SCREEN])] : [getFullScreenConfig(actions[_top_nav_ids.TopNavIds.FULL_SCREEN]), getShareConfig(), getCloneConfig(actions[_top_nav_ids.TopNavIds.CLONE]), getEditConfig(actions[_top_nav_ids.TopNavIds.ENTER_EDIT_MODE]), getFollowConfig(actions[_top_nav_ids.TopNavIds.FOLLOW])];
    case _dashboard_view_mode.DashboardViewMode.EDIT:
      return [getSaveConfig(), getViewConfig(actions[_top_nav_ids.TopNavIds.EXIT_EDIT_MODE]), getAddConfig(), getOptionsConfig(), getShareConfig()];
    default:
      return [];
  }
}

